export function FlagIcon({
  region,
  className,
}: {
  region: "usa" | "europe" | (string & {});
  className?: string;
}) {
  switch (region) {
    case "usa":
      return <FlagUSA className={className} />;
    case "europe":
      return <FlagEurope className={className} />;
    default:
      return null;
  }
}

